import React, { useState } from 'react';
import {Typography, Button, Form, message, Input, Icon} from 'antd';
import Dropzone from 'react-dropzone';
import Axios from 'axios';
import { useSelector } from 'react-redux';

const {TextArea} = Input;
const {Title} = Typography;

const PrivateOptions = [
    {value:0, label:"Private"},
    {value:1, label:"Publuc"}
]
const CategoryOptions = [
    {value:0, label:"Film & Animation"},
    {value:1, label:"Autos & Vehicles"},
    {value:2, label:"Music"},
    {value:3, label:"Pets & Animals"},
]

function VideoUploadPage() {
    const user = useSelector(state => state.user)

    //state 안에 value 저장하기
    const [VideoTitle, setVieoTitle] = useState("");
    const [Description, setDescription] = useState("");
    const [Private, setPrivate] = useState(0);
    const [Category, setCategory]=useState("Film & Animation");
    //비디오,썸네일 파일Path와 정보 저장
    const [FilePath, setFilePath] = useState("");
    const [Duration, setDuration] = useState("");
    const [ThumbnailPath, setThumbnailPath] = useState("");

    const onTitleChange = (e) =>{
        setVieoTitle(e.currentTarget.value)
    }

    const onDescriptionChange = (e) =>{
        setDescription(e.currentTarget.value)
    }

    const onPrivateChange = (e)=>{
        setPrivate(e.currentTarget.value)
    }

    const onCategoryChange = (e)=>{
        setCategory(e.currentTarget.value)
    }

    const onDrop = (files)=>{
        let formData = new FormData();
        const config = {
            header:{'content-type':'multipart/form-data'}
        }
        formData.append("file", files[0])
        Axios.post('/api/video/uploadfiles', formData, config)
            .then(response=>{
                if(response.data.success){
                    console.log(response.data)

                    let variable={ //서버에서 받은 url과 파일이름을 변수에 저장
                        url: response.data.url,
                        filename: response.data.filename
                    }

                    setFilePath(response.data.url)

                    Axios.post('/api/video/thumbnail', variable)
                        .then(response=>{
                            if(response.data.success){
                                console.log(response.data)
                                setDuration(response.data.fileDuration);
                                setThumbnailPath(response.data.url);
                            }else{
                                alert('썸네일 생성에 실패했습니다.')
                            }
                        })
                }else{
                    alert('비디오 업로드를 실패했습니다.')
                }
            })
    }

    const onSubmit = (e)=>{
        e.preventDefault();

        const variables = {
            writer: user.userData._id,
            title: VideoTitle,
            description: Description,
            privacy: Private,
            filePath: FilePath,
            category: Category,
            duration: Duration,
            thumbnail: ThumbnailPath,
        }
        Axios.post('/api/video/uploadVideo', variables)
    }

    return (
        <div>
            <div style={{maxWidth:'700px', margin:'2rem auto'}}>
                <div style={{textAlign:'center', marginBottom:'2rem'}}>
                    <Title level={2}>Upload Video</Title>
                </div>

                <Form  onSubmit={onSubmit}>
                    <div style={{display:'flex', justifyContent:'space-between'}}>
                        {/* Drop Zone */}
                        <Dropzone onDrop={onDrop} multiple={false} maxSize={100000000}>
                            {({getRootProps,getInputProps})=>(
                                <div style={{width:'300px', height:'240px', border:'1px solid lightgray', display:'flex', alignItems:'center', justifyContent:'center'}} {...getRootProps()}>
                                    <input {...getInputProps()}/>
                                    <Icon type='plus' style={{fontSize:'3rem'}}/>
                                </div>
                            )}
                        </Dropzone>

                        {/* Thumbnail Zone */}
                        {ThumbnailPath &&
                            <div>
                                <img src={`http://localhost:5000/${ThumbnailPath}`} alt="thumbnnail"/>
                            </div>
                        }
                    </div>

                    <br/>
                    <br/>
                    <label>Title</label>
                    <Input onChange={onTitleChange} value={VideoTitle}/>

                    <br/>
                    <br/>
                    <label>Description</label>
                    <TextArea onChange={onDescriptionChange} value={Description} />

                    <br/>
                    <br/>
                    <select onChange={onPrivateChange}>
                        {PrivateOptions.map((item, index)=>(
                            <option key={index} value={item.value}>{item.label}</option>
                        ))}
                    </select>

                    <br/>
                    <br/>
                    <select onChange={onCategoryChange}>
                        {CategoryOptions.map((item, index) => (
                            <option key={index} value={item.value}>{item.label}</option>
                        ))}
                    </select>

                    <br/>
                    <br/>
                    <Button type="primary" size="large" onClick={onSubmit}>
                        Submit
                    </Button>
                
                </Form>
            </div>
        </div>
    );
}

export default VideoUploadPage;
