import axios from "axios";
import React, { useEffect, useState } from "react";
import { Card, Row, Col } from "antd";

const { Meta } = Card;

function SearchData() {
    const [SearchItem, setSearchItem] = useState("");

    useEffect(() => {
      let body = {
        start: Start,
        end: End,
      };
  
      commonAxios(body);
    }, []);
  
    //더 보기에 두 번 들어가기 때문에 따로 분리
    const commonAxios = (body) => {
      //post에 info값 넣을 것
      axios.post("/api/video/searchvideo", body).then((response) => {
        if (response.data.success) {
          // console.log(response.data);
  
          if (body.showMore) {
            setDatas([...Datas, ...response.data.videoInfo]);
          } else {
            setDatas(response.data.videoInfo);
          }
          setLimitImage(response.data.limitImage);
        } else {
          alert("정보를 가져오는데 실패했습니다.");
        }
      });
    };
  
   
    const updateSearchItem = (newSearchItem) => {
      let body = {
        start: 0,
        end: End,
        filters: Filters,
        searchItem: newSearchItem,
      };
  
      setStart(0);
      setSearchItem(newSearchItem);
      commonAxios(body);
    };
  
    return (
      <div style={{ width: "100%", margin: "0" }}>
        <br />
        <br />
        <br />
        <br />
        <h2 style={{ textAlign: "center" }}>DB에 저장한 거 확인하기</h2>
  
        <Row>
          {/* checkBox */}
          <Col lg={10} xs={20} style={{ position: "relative", left: "8.5%" }}>
            <DataCheckbox
              list={seasons}
              boxFilters={(filters) => boxFilters(filters, "seasons")}
            />
          </Col>
          {/* radioBox */}
          <Col lg={10} xs={20} style={{ position: "relative", left: "2%" }}>
            {/* RadioBox */}
            <DataRadioBox
              list={price}
              boxFilters={(filters) => boxFilters(filters, "price")}
            />
          </Col>
        </Row>
  
        {/* search */}
        <div
          style={{
            display: "flex",
            justifyContent: "flex-end",
            margin: "1rem auto",
            width: "65%",
          }}
        >
          <DataSearch refreshFunction={updateSearchItem} /> //추가
        </div>
  
        {/* card */}
        <div style={{ width: "85%", margin: "1rem auto" }}>
          <Row gutter={[20, 20]}>{renderCard}</Row>
        </div>
  
        {LimitImage >= End && (
          <div style={{ display: "flex", justifyContent: "center" }}>
            <button style={{}} onClick={showMoreData}>
              더 보기
            </button>
          </div>
        )}
      </div>
    );
  
}

export default SearchData;
