import React,{useState, useEffect} from 'react';
import { Menu } from 'antd';
import { Input, Space } from 'antd';
import Axios from 'axios';

const { Search } = Input;


function SearchUser(props) {
    const [keyword, setKeyword] = useState('%^&');
    const [UserName, setUserName] = useState([])

    const handleSearch = (event) => {
        if (event.target.value === '') {
          setKeyword('%^&');
        } else {
          setKeyword(event.target.value);
        }
    };

    const a = localStorage.getItem('user')

    useEffect(()=>{
        Axios.get('/api/user/userSearch')
          .then(response=>{
            if(response.data.success){
                console.log(response.data.doc)
                // setVideo(response.data.videos)
            }else{
              alert('유저 검색에 실패했습니다.')
            }
          })
    },[])

    // const userToken = localStorage.getItem('Token');
    // fetch(`${API_ENDPOINT}/user/searchuser/?keyword=${keyword}`, {
    //     method: 'GET',
    //     headers: {
    //         Authorization: `Bearer ${userToken}`,
    //         'Content-type': 'application/json',
    //     },
    // })


    return (
        <Menu mode={props.mode} className="searchBox" >
            <Menu.Item key="subscription" style={{width:'45%', height: '68px', padding:'14px 0px 14px 30px' , margin:'0'}}>
                <Search className="searchInput"
                    placeholder="input search creater"
                    allowClear
                    enterButton="Search"
                    size="large"
                    onChange={handleSearch}
                />
            </Menu.Item>
        </Menu>
    )
}

export default SearchUser;
