import React, { useEffect, useState } from 'react';
import SingleComment from './SingleComment';

function ReplyComment(props) {
    const [ChildCommentNumber, setChildCommentNumber] = useState(0)
    const [OpenReplyComments, setOpenReplyComments] = useState(false)

    useEffect(()=>{
        let commentNumber = 0;

        props.commentLists.map((comment)=>{
            if(comment.responseTo === props.parentCommentId){
                commentNumber ++
            }
        })

        setChildCommentNumber(commentNumber)

    },[props.CommentLists, props.parentCommentId])


    const renderReplyComment = (parentCommentId)=>{
        props.commentLists.map((comment, index) => (
            <React.Fragment>
                {comment.responseTo === parentCommentId &&
                    <div key={index} style={{width:'80%', marginLeft:'40px'}}>
                        <SingleComment comment={comment} postId={props.videoId} refreshFunction={props.refreshFunction} />
                        <ReplyComment commentLists={props.commentLists} postId={props.videoId} parentCommentId={comment._id}/>
                    </div>
                }
            </React.Fragment>
        ))
    }

    const onHandleChange = ()=>{
        setOpenReplyComments(!OpenReplyComments)
    }

    return (
        <div>
            {ChildCommentNumber > 0 &&
                <p style={{ fontSize: '14px', color: 'gray', margin: 0 }} onClick={onHandleChange}>
                    View {ChildCommentNumber} more comment(s)
                </p>
            }
            {OpenReplyComments &&
                renderReplyComment(props.parentCommentId)
            }

        </div>
    );
}

export default ReplyComment;
