import React from 'react';

function ReplyComment(props) {
    const renderReplyComment = ()=>{
        props.commentLists.map((comment, index) => {
            <React.Fragment>
                <SingleComment comment={comment} postId={videoId} refreshFunction={props.refreshFunction} />
                <ReplyComment commentLists={props.commentLists} />
            </React.Fragment>
        })
    }
    return (
        <div>
            <p style={{fontSize:'14px', color:'gray', margin:0}} onClick>
                View 1 more comment(s)
            </p>
        </div>
    );
}

export default ReplyComment;
