import React,{useState, useEffect} from 'react';
import { Menu } from 'antd';
import { Input} from 'antd';
import Axios from 'axios';

const { Search } = Input;


function SearchVideo(props) {
  const [SearchVideo, setSearchVideo] = useState("");

  const searchHandler = (event) => {
    setSearchVideo(event.currentTarget.value);
    props.refreshFunction(event.currentTarget.value);
  };

    useEffect(()=>{
        Axios.post('/api/video/searchvideo')
          .then(response=>{
            if(response.data.success){
                console.log(response.data)
                // setVideo(response.data.videos)
            }else{
              alert('유저 검색에 실패했습니다.')
            }
          })
    },[])

    return (
        <Menu mode={props.mode} className="searchBox" >
            <Menu.Item key="subscription" style={{width:'45%', height: '68px', padding:'14px 0px 14px 30px' , margin:'0'}}>
                <Search 
                    className="searchInput" allowClear
                    enterButton="Search" size="large"
                    type="text" name="searchText"
                    placeholder="검색어를 입력하세요"
                    value={SearchVideo}
                    onChange={searchHandler}
                />
                
            </Menu.Item>
        </Menu>
    )
}

export default SearchVideo;
