import Axios from 'axios';
import React, { useEffect, useState } from 'react';

function Subscribe(props) {
    const [SubscribeNumber, setSubscribeNumber] = useState()
    const [Subscribed, setSubscribed] = useState()

    useEffect(()=>{
        let variable = {userTo:props.userTo}
        let subvariable = {userTo:props.userTo, userForm: localStorage.getItem('userId')}

        Axios.post('/api/subscribe/subscribeNumber', variable)
            .then(response => {
                if(response.data.success){
                    setSubscribeNumber(response.data.SubscribeNumber);
                }else{
                    alert('구독자 수를 받아오지 못했습니다.')
                }
            })
        
        Axios.post('/api/subscribe/subscribed', subvariable)
            .then(response=>{
                if(response.data.success){
                    setSubscribed(response.data.subscribed)
                }else{
                    alert('정보를 받아오지 못했습니다.')
                }
            })
    })
    return (
        <div>
            <button
                style={{backgroundColor:'#CC0000', borderRadius:'4px', color:'white', padding: '10px 16px',
                        fontWeight:'500', fontSize:'1rem', textTransform:'uppercase', border:'none'
                }}
                onClick  
            >
               {SubscribeNumber} Subscribe
            </button>
        </div>
    );
}

export default Subscribe;
