import Axios from 'axios';
import React,{useState} from 'react';
import {useSelector} from 'react-redux'
import SingleComment from './SingleComment';
import ReplyComment from './ReplyComment';

function Comment(props) {
    const user = useSelector(state => state.user);
    const [commentValue, setCommentValue] = useState("")

    const handleClick = (event)=>{
        setCommentValue(event.currentTarget.value)
    }

    const onSubmit = (event) => {
        event.preventDefault();

        const variables = {
            content: commentValue,
            writer: user.userData._id,
            postId: props.postId
        }

        Axios.post('/api/comment/saveComment', variables)
            .then(response => {
                if (response.data.success) {
                    console.log(response.data.result)
                    setCommentValue("") //초기화
                    props.refreshFunction(response.data.result) //save한 comment의 정보를 부모 component에서 받을 수 있게!
                } else {
                    alert('코멘트 등록에 실패했습니다.')
                }
            })
    }

    return (
        <div style={{marginTop:'1.5rem'}}>
            <br/>
            <p>댓글</p>
            <hr/>

            {/* Comment Lists */}
            {/* {console.log(props.commentLists)} */}

            {props.commentLists && props.commentLists.map((comment, index)=>(
                (!comment.responseTo && 
                    <React.Fragment  key={comment._id}>
                        <SingleComment comment={comment} postId={props.postId} refreshFunction={props.refreshFunction} />
                        <ReplyComment parentCommentId={comment._id} commentLists={props.commentLists} postId={props.postId} refreshFunction={props.refreshFunction}/>
                    </React.Fragment>
                )
            ))}



            {/* Root Comment Form */}
            <form style={{display:'flex',marginTop:'1rem' }} onSubmit={onSubmit} >
                <textarea
                    style={{width:'100%', borderRadius:'5px'}}
                    onChange={handleClick}
                    value={commentValue}
                    placeholder="코멘트를 작성해 주세요"
                />
                <br/>
                <button style={{width:'20%', height:'52px'}} onClick={onSubmit}>Submit</button>
            </form>
        </div>
    );
}

export default Comment;
