import React from 'react';
import Axios from 'axios';
import { Popconfirm, message } from 'antd';

const confirm = (e, targetedVideoId) => {
    console.log(e);
    message.success('Click on Yes');
    //비디오 삭제 기능 => 삭제 후 메인페이지로 이동
    const variables = {
        videoId: targetedVideoId
    }

    Axios.post(`/api/video/deletevideo`, variables)
        .then(response => {
            if (response.data.success) {
                message.success('비디오가 삭제되었습니다.')

                setTimeout(()=>{
                    props.history.push('/')
                },3000)
            } else {
                alert('비디오를 삭제하지 못했습니다.')
            }
        })

    
}

const cancel = (e) => {
  console.log(e);
  message.error('Click on No');
}

function VideoDelete(props) {
    return (
        <div>
            <Popconfirm title="Are you sure delete this video?" onConfirm={()=>confirm(props.videoId)} onCancel={cancel} okText="Yes" cancelText="No">
                <button
                    style={{
                        backgroundColor: 'black', borderRadius: '4px', color: 'white', padding: '8px 16px',
                        fontWeight: '500', fontSize: '1rem', textTransform: 'uppercase', border: 'none'
                    }}
                >
                    Delete
                </button>
            </Popconfirm>

        </div>
    );
}

export default VideoDelete;
